mapper.update:
UPDATE tb_order_config
SET activate_count=activate_count+1
WHERE order_id=#{orderId}

mapper.select:
SELECT a.company_id, b.company_name, COUNT(a.order_id) ordercount, SUM(a.licence_count) licencecount, SUM(a.activate_count) activatecount FROM tb_order_config a INNER JOIN tb_company b ON a.company_id=b.company_id
a.company_id=#{companyId}
AND (order_date >= #{orderDateBegin} AND order_date <= #{orderDateEnd})
